import React, { useEffect, useState } from "react";
import NoteList from "../components/Notes/NoteList";
import NoteForm from "../components/Notes/NoteForm";
import { fetchNotes, createNote } from "../services/notesService";

const NotesPage = () => {
    const [notes, setNotes] = useState([]);

    // Завантаження нотаток
    useEffect(() => {
        const loadNotes = async () => {
            try {
                const fetchedNotes = await fetchNotes();
                setNotes(fetchedNotes);
            } catch (error) {
                console.error("Помилка при завантаженні нотаток:", error);
            }
        };

        loadNotes();
    }, []);

    // Додавання нотатки
    const handleCreateNote = async (noteData) => {
        try {
            const newNote = await createNote(noteData);
            setNotes((prevNotes) => [newNote, ...prevNotes]);
        } catch (error) {
            console.error("Помилка при створенні нотатки:", error);
        }
    };

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">Мої Нотатки</h1>
            <div className="mb-4">
                <NoteForm onCreate={handleCreateNote} />
            </div>
            <NoteList notes={notes} />
        </div>
    );
};

export default NotesPage;
