import React, { useEffect, useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import AuthProvider, { AuthContext } from "./context/AuthContext";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import NotesPage from "./pages/NotesPage";
import EditNotePage from "./pages/EditNotePage";
import NotFoundPage from "./pages/NotFoundPage";
import "./styles/global.css";
import { testConnection } from "./services/api"; // Імпортуємо функцію для тестового запиту

// Компонент для приватних маршрутів
const PrivateRoute = ({ children }) => {
    const { isAuthenticated } = useContext(AuthContext);
    return isAuthenticated ? children : <Navigate to="/login" />;
};

// Основний компонент додатка
const App = () => {
    // Перевірка підключення до сервера
    useEffect(() => {
        const checkServerConnection = async () => {
            try {
                await testConnection(); // Виклик функції тестового запиту
            } catch (error) {
                console.error("Не вдалося підключитися до сервера:", error.message);
            }
        };

        checkServerConnection(); // Викликаємо функцію
    }, []); // Порожній масив залежностей

    return (
        <AuthProvider>
            <Router>
                <Routes>
                    {/* Публічні маршрути */}
                    <Route path="/login" element={<LoginPage />} />
                    <Route path="/register" element={<RegisterPage />} />

                    {/* Приватні маршрути */}
                    <Route
                        path="/"
                        element={
                            <PrivateRoute>
                                <NotesPage />
                            </PrivateRoute>
                        }
                    />
                    <Route
                        path="/notes/:id/edit"
                        element={
                            <PrivateRoute>
                                <EditNotePage />
                            </PrivateRoute>
                        }
                    />

                    {/* Маршрут для помилок */}
                    <Route path="*" element={<NotFoundPage />} />
                </Routes>
            </Router>
        </AuthProvider>
    );
};

export default App;
