import React, { useEffect, useState } from "react";
import notesService from "../services/notesService";
import NoteForm from "../components/Notes/NoteForm";
import NoteList from "../components/Notes/NoteList";

const NotesPage = () => {
    const [notes, setNotes] = useState([]);

    const fetchNotes = async () => {
        try {
            const data = await notesService.getNotes();
            setNotes(data);
        } catch (err) {
            console.error("Failed to fetch notes", err);
        }
    };

    const handleCreate = async (newNote) => {
        try {
            const createdNote = await notesService.createNote(newNote);
            setNotes([createdNote, ...notes]);
        } catch (err) {
            console.error("Failed to create note", err);
        }
    };

    const handleEdit = async (updatedNote) => {
        try {
            const { id, ...rest } = updatedNote;
            const updated = await notesService.updateNote(id, rest);
            setNotes(notes.map((note) => (note.id === id ? updated : note)));
        } catch (err) {
            console.error("Failed to update note", err);
        }
    };

    const handleDelete = async (id) => {
        try {
            await notesService.deleteNote(id);
            setNotes(notes.filter((note) => note.id !== id));
        } catch (err) {
            console.error("Failed to delete note", err);
        }
    };

    useEffect(() => {
        fetchNotes();
    }, []);

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">Мої нотатки</h1>
            <NoteForm onCreate={handleCreate} />
            <NoteList notes={notes} onEdit={handleEdit} onDelete={handleDelete} />
        </div>
    );
};

export default NotesPage;
