import React, { useState } from "react";
import { useParams } from "react-router-dom";
import NoteEditForm from "../components/Notes/NoteEditForm";

const EditNotePage = () => {
    const { id } = useParams();
    const [note, setNote] = useState(null);

    // Симуляція отримання даних нотатки. Замініть на реальний запит.
    const fetchNote = async () => {
        // Наприклад, замініть на запит до вашого API.
        const fetchedNote = { id, title: "Example Title", content: "Example Content" };
        setNote(fetchedNote);
    };

    useState(() => {
        fetchNote();
    }, [id]);

    if (!note) return <p>Loading...</p>;

    return (
        <div>
            <h1>Edit Note</h1>
            <NoteEditForm note={note} onUpdate={() => {}} onCancel={() => {}} />
        </div>
    );
};

export default EditNotePage;
