import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import LoginForm from "../components/Auth/LoginForm";

const LoginPage = () => {
    const { login } = useContext(AuthContext);

    return (
        <div className="vh-100 d-flex justify-content-center align-items-center bg-light">
            <div className="card shadow-lg rounded p-4" style={{ maxWidth: "400px", width: "90%" }}>
                <h1 className="text-center text-primary fw-bold mb-4">Welcome Back</h1>
                <p className="text-center text-muted mb-4">Log in to access your account</p>
                <LoginForm onLogin={login} />
                <div className="text-center mt-4">
                    <span className="text-muted">Don't have an account? </span>
                    <Link to="/register" className="text-decoration-none text-primary fw-semibold">
                        Register here
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
