import React, { useState, useEffect } from "react";
import axios from "axios";

const NotesPage = () => {
    const [notes, setNotes] = useState([]);
    const [newNote, setNewNote] = useState({ title: "", content: "" });
    const [editingNote, setEditingNote] = useState(null);

    const fetchNotes = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("/api/notes", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotes(response.data);
        } catch (err) {
            console.error("Failed to fetch notes", err);
        }
    };

    const handleCreate = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            const response = await axios.post(
                "/api/notes",
                newNote,
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setNotes([...notes, response.data]);
            setNewNote({ title: "", content: "" });
        } catch (err) {
            console.error("Failed to create note", err);
        }
    };

    const handleDelete = async (id) => {
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`/api/notes/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotes(notes.filter((note) => note.id !== id));
        } catch (err) {
            console.error("Failed to delete note", err);
        }
    };

    const handleUpdate = async (id, updatedNote) => {
        try {
            const token = localStorage.getItem("token");
            await axios.put(
                `/api/notes/${id}`,
                updatedNote,
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setNotes(notes.map((note) => (note.id === id ? { ...note, ...updatedNote } : note)));
            setEditingNote(null);
        } catch (err) {
            console.error("Failed to update note", err);
        }
    };

    useEffect(() => {
        fetchNotes();
    }, []);

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">Мої нотатки</h1>

            {/* Форма створення нової нотатки */}
            <form onSubmit={handleCreate} className="mb-4">
                <div className="mb-3">
                    <input
                        type="text"
                        placeholder="Назва"
                        className="form-control"
                        value={newNote.title}
                        onChange={(e) => setNewNote({ ...newNote, title: e.target.value })}
                    />
                </div>
                <div className="mb-3">
                    <textarea
                        placeholder="Опис"
                        className="form-control"
                        value={newNote.content}
                        onChange={(e) => setNewNote({ ...newNote, content: e.target.value })}
                    ></textarea>
                </div>
                <button className="btn btn-primary">Додати нотатку</button>
            </form>

            {/* Вивід нотаток */}
            <div className="row">
                {notes.map((note) => (
                    <div className="col-md-6 mb-4" key={note.id}>
                        <div className="card p-3 shadow-sm">
                            {editingNote?.id === note.id ? (
                                <>
                                    {/* Редагування нотатки */}
                                    <input
                                        type="text"
                                        className="form-control mb-2"
                                        value={editingNote.title}
                                        onChange={(e) =>
                                            setEditingNote({ ...editingNote, title: e.target.value })
                                        }
                                    />
                                    <textarea
                                        className="form-control mb-2"
                                        value={editingNote.content}
                                        onChange={(e) =>
                                            setEditingNote({ ...editingNote, content: e.target.value })
                                        }
                                    ></textarea>
                                    <button
                                        className="btn btn-success me-2"
                                        onClick={() => handleUpdate(note.id, editingNote)}
                                    >
                                        Зберегти
                                    </button>
                                    <button
                                        className="btn btn-secondary"
                                        onClick={() => setEditingNote(null)}
                                    >
                                        Відмінити
                                    </button>
                                </>
                            ) : (
                                <>
                                    {/* Перегляд нотатки */}
                                    <h5 className="card-title">{note.title}</h5>
                                    <p className="card-text">{note.content}</p>
                                    <button
                                        className="btn btn-warning me-2"
                                        onClick={() => setEditingNote(note)}
                                    >
                                        Змінити
                                    </button>
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => handleDelete(note.id)}
                                    >
                                        Видалити
                                    </button>
                                </>
                            )}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default NotesPage;
