import React, { useState, useEffect } from "react";
import axios from "axios";
import NoteCard from "../components/Notes/NoteCard";

const NotesPage = () => {
    const [notes, setNotes] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [currentNote, setCurrentNote] = useState(null);

    // Завантаження нотаток
    const fetchNotes = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("/api/notes", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotes(response.data);
        } catch (err) {
            console.error("Failed to fetch notes", err);
        }
    };

    // Видалення нотатки
    const handleDelete = async (id) => {
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`/api/notes/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setNotes(notes.filter((note) => note.id !== id));
        } catch (err) {
            console.error("Failed to delete note", err);
        }
    };

    // Відкриття модального вікна для створення/редагування
    const handleOpenModal = (note = null) => {
        setCurrentNote(note);
        setShowModal(true);
    };

    // Закриття модального вікна
    const handleCloseModal = () => {
        setCurrentNote(null);
        setShowModal(false);
    };

    useEffect(() => {
        fetchNotes();
    }, []);

    return (
        <div className="container mt-4">
            <h1 className="text-center mb-4">Мої нотатки</h1>
            <button className="btn btn-primary mb-4" onClick={() => handleOpenModal()}>
                Додати нотатку
            </button>
            <div className="row">
                {notes.map((note) => (
                    <div className="col-md-6" key={note.id}>
                        <NoteCard note={note} onEdit={handleOpenModal} onDelete={handleDelete} />
                    </div>
                ))}
            </div>

            {showModal && (
                <NoteModal
                    note={currentNote}
                    onClose={handleCloseModal}
                    onRefresh={fetchNotes}
                />
            )}
        </div>
    );
};

export default NotesPage;
