import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import LoginForm from "../components/Auth/LoginForm";

const LoginPage = () => {
    const { login } = useContext(AuthContext);

    return (
        <div
            className="d-flex justify-content-center align-items-center vh-100 bg-light"
            style={{
                background: "linear-gradient(135deg, #f8f9fa, #e9ecef)",
            }}
        >
            <div
                className="card p-5 shadow rounded"
                style={{
                    maxWidth: "400px",
                    width: "100%",
                    background: "white",
                }}
            >
                <h2 className="text-center text-primary mb-4">Welcome Back</h2>
                <p className="text-center text-muted mb-4">Log in to your account</p>
                <LoginForm onLogin={login} />
                <div className="text-center mt-4">
                    <span className="text-muted">Don't have an account?</span>{" "}
                    <Link to="/register" className="text-decoration-none text-primary fw-bold">
                        Register here
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
