import React, { useState } from "react";
import NoteList from "../components/Notes/NoteList";
import NoteForm from "../components/Notes/NoteForm";
import NoteEditForm from "../components/Notes/NoteEditForm";

const NotesPage = () => {
    const [editingNote, setEditingNote] = useState(null);

    const handleEdit = (note) => setEditingNote(note);
    const handleCancelEdit = () => setEditingNote(null);

    return (
        <div className="container mt-5">
            <h1 className="text-center mb-4">Your Notes</h1>
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    {editingNote ? (
                        <NoteEditForm
                            note={editingNote}
                            onUpdate={handleCancelEdit}
                            onCancel={handleCancelEdit}
                        />
                    ) : (
                        <NoteForm onCreate={() => {}} />
                    )}
                    <NoteList onEdit={handleEdit} />
                </div>
            </div>
        </div>
    );
};

export default NotesPage;
